ound:#fff;left:-40px;animation:scan 2s infinite linear}.desc{color:rgba(255,255,255,.6);font-size:.9rem;letter-spacing:4px;font-weight:500;text-transform:uppercase}.brand-footer{margin-top:50px;color:var(--a);font-weight:900;font-size:.75rem;letter-spacing:5px;animation:blink 2s infinite}@keyframes cardFloat{0%,100%{transform:translateY(0) rotateY(0) rotateX(0)}33%{transform:translateY(-20px) rotateY(5deg) rotateX(-5deg)}66%{transform:translateY(10px) rotateY(-5deg) rotateX(5deg)}}@keyframes textPulse{from{filter:drop-shadow(0 0 10px var(--p));transform:scale(1)}to{filter:drop-shadow(0 0 40px var(--a));transform:scale(1.05)}}@keyframes scan{0%{left:-40px}100%{left:140px}}@keyframes blink{0%,100%{opacity:.4}50%{opacity:1}}</style><canvas id=canvas></canvas><div class=viewport><div class=error-card><div class=glitch-num>404</div><div class=title>System Void</div><div class=line-anim></div><p class=desc>Koordinat tidak ditemukan dalam database<div class=brand-footer>PT JASA HARAMAIN</div></div></div><script>const canvas=document.getElementById("canvas"),ctx=canvas.getContext("2d");let particles=[];const particleCount=350;function init(){canvas.width=window.innerWidth,canvas.height=window.innerHeight,particles=[];for(let t=0;t<350;t++)particles.push({x:Math.random()*canvas.width,y:Math.random()*canvas.height,vx:.8*(Math.random()-.5),vy:.8*(Math.random()-.5),size:2*Math.random(),color:Math.random()>.5?"#0d47a1":"#42a5f5"})}function draw(){ctx.fillStyle="rgba(0, 5, 10, 0.2)",ctx.fillRect(0,0,canvas.width,canvas.height),particles.forEach(((t,a)=>{t.x+=t.vx,t.y+=t.vy,(t.x<0||t.x>canvas.width)&&(t.vx*=-1),(t.y<0||t.y>canvas.height)&&(t.vy*=-1),ctx.beginPath(),ctx.arc(t.x,t.y,t.size,0,2*Math.PI),ctx.fillStyle=t.color,ctx.fill();for(let n=a+1;n<particles.length;n++){const a=particles[n],c=t.x-a.x,e=t.y-a.y,i=Math.sqrt(c*c+e*e);i<100&&(ctx.strokeStyle=t.color,ctx.globalAlpha=.3*(1-i/100),ctx.lineWidth=.5,ctx.beginPath(),ctx.moveTo(t.x,t.y),ctx.lineTo(a.x,a.y),ctx.stroke(),ctx.globalAlpha=1)}})),requestAnimationFrame(draw)}window.addEventListener("resize",init),init(),draw()</script>
